(window.webpackJsonp=window.webpackJsonp||[]).push([[13],{541:function(n,h,t){"use strict";t.r(h);var c=t(8),a=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h2",{attrs:{id:"predictive-analytics-la-gi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#predictive-analytics-la-gi"}},[n._v("#")]),n._v(" Predictive Analytics là gì?")]),n._v(" "),t("p",[n._v("Đây là một thuật ngữ ám chỉ việc dùng dữ liệu trong khóa khứ, các thuật toán thống kê và học máy với mục tiêu là để dự đoán những gì có thể xuất hiện trong tương lại.\nBằng việc áp dụng kỹ thuật này người ta có thể dự đoán xu thế, giảm rủi do, tăng doanh số cho business. Nói một cách khác, khi có thật nhiều dữ liệu,\nchúng ta có thể trả lời được câu hỏi liệu chuyện gì có khả năng cao sẽ diễn ra, và chúng ta có thể làm gì để đối ứng với việc đó."),t("br"),n._v("\nVới nhiều công ty, predictive analytics không còn là gì mới mẻ, nhưng gần đây, cùng với xu thế BigData thì công nghệ này càng trở lên hót hơn.\nDưới đây sẽ là một số ví dụ áp dụng predictive analytic trong trong cuộc sống của chúng ta.")]),n._v(" "),t("ul",[t("li",[n._v("Các nhà nghiên cứu của các trường đại học dự đoán khả năng ly hôn của các cặp vợ chồng, dựa trên nhũng dứ liệu đã thu thập được từ nhiều năm trước.")]),n._v(" "),t("li",[n._v("Facebook và Linkedln dự đoán các kết nối cá nhân, đưa ra cho bạn danh sách những người bạn có thể kết bạn.")]),n._v(" "),t("li",[n._v("Các trang kết nối hẹn hò cố gắng đưa ra dự đoán các cặp có khả năng hẹn hò, phù hợp với nhau dẫn đến khả năng kết hôn cao.")]),n._v(" "),t("li",[n._v("Amazon dự đoán nhu cầu mua sắm của khách hàng, kết quả là 35% lượng hàng bán của Amazon đến từ recommendation system.")]),n._v(" "),t("li",[n._v("Telenor: dự đoán khách hàng nào sắp rời bỏ sẽ rời bỏ công ty của mình để có chiến lược giữ chân khách hàng.")])]),n._v(" "),t("h2",{attrs:{id:"đe-xay-dung-mot-predictive-analytics-system-gom-7-buoc-chinh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#đe-xay-dung-mot-predictive-analytics-system-gom-7-buoc-chinh"}},[n._v("#")]),n._v(" Để xây dựng một Predictive Analytics System gồm 7 bước chính")]),n._v(" "),t("h3",{attrs:{id:"đinh-nghia"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#đinh-nghia"}},[n._v("#")]),n._v(" Định nghĩa")]),n._v(" "),t("p",[n._v("Bước này gồm rất nhiều định nghĩa buộc ta phải nêu rõ ngay từ ban đầu, ví dụ như: Mục tiêu của hệ thống là gì,\nđộ chính xác bao nhiêu? Ảnh hưởng thế nào, bao nhiêu đến Business? Những dữ liệu nào sẽ được dùng? etc.")]),n._v(" "),t("h3",{attrs:{id:"thu-thap-du-lieu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#thu-thap-du-lieu"}},[n._v("#")]),n._v(" Thu thập dữ liệu")]),n._v(" "),t("p",[n._v("Thu thập càng nhiều và đa dạng thì càng tốt. Khi không có dữ liệu nhiều và đa dạng thì thuật toán có tốt đi nữa cũng khó cho kết quả dự đoán tốt.")]),n._v(" "),t("h3",{attrs:{id:"phan-tich-du-lieu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#phan-tich-du-lieu"}},[n._v("#")]),n._v(" Phân tích dữ liệu")]),n._v(" "),t("p",[n._v("Ở bước này, ta cần phân tích và chiết lọc ra các giá trị cốt lõi nằm rải rác đâu đó trong dữ liệu.\nHay tìm ra các mối liên hệ giữa những thuộc tính của dữ liệu, từ đó lập ra các giả thiết.")]),n._v(" "),t("h3",{attrs:{id:"thong-ke"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#thong-ke"}},[n._v("#")]),n._v(" Thống kê")]),n._v(" "),t("p",[n._v("Ở bước này, dùng xác suất thống kê để kiểm chứng lại các giả thiết được lập ra ở bước trên. Đa phần mọi người thường bỏ qua bước này,\nmà chỉ phân tích dữ liệu đưa ra các đồ thì rồi đưa kết luận. Thực sự sau khi phân tích thì thứ mình đưa ra chỉ có thể là giả thiết,\ngiả thiết đó thuyết phục được hay không thì rất cần có xác xuất thống kê kiểm định lại. Chính vì thế bước này là một bước khó trong khi xây dựng hệ thống.")]),n._v(" "),t("h3",{attrs:{id:"xay-dung-mo-hinh-du-đoan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#xay-dung-mo-hinh-du-đoan"}},[n._v("#")]),n._v(" Xây dựng mô hình dự đoán")]),n._v(" "),t("p",[n._v("Đây là phần lõi của hệ thống. Để cho độ chính xác của hệ thống được cao, người ta không dùng một thuật toán cố định nào cả,\nmà thường sẽ kết hợp nhiều thuật toán với nhau, kết quả dự đoán cuối cùng sẽ là kết quả dự đoán của nhiều thuật thoán, nhiều mô hình kết hợp lại với nhau.\nPhương pháp này có tên là Ensemble method.\n(ở đây nói qua về Ensemble một chút: Tại sao trong học máy, Ensemble method lại có tính hữu dụng cao?\nVì đơn giản nó giống như một phương pháp biểu quyết rồi quyết định theo số đông để đưa ra kết quả có độ tự tin cao hơn. Giả sử ta có 25 mô hình học máy,\nmỗi một mô hình sẽ có xác suất đưa ra dự đoán sai là 0.35. Vậy nếu theo phương pháp biểu quyết theo số đông, khi ta kết hợp các mô hình này lại để dự đoán,\nsẽ cần ít nhất 13 mô hình cho ra kết quả sai, ta mới đưa ra kết luận là kết quả dự đoán của mô hình là sai. Và tất nhiên khí đó,\nxác xuất để 13 mô hình đều dự đoán sai sẽ dễ dàng tính được bằng 0.06.\nSo sánh xác suất dự đoán sai của mô hình đã áp dụng Ensemble method (0.06) và xác suất dự đoán sai của mô hình đơn lẻ ban đầu (0.35)\nta thấy rất rõ được sức mạnh của Ensemble method.)")]),n._v(" "),t("h3",{attrs:{id:"deploy"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#deploy"}},[n._v("#")]),n._v(" Deploy")]),n._v(" "),t("p",[n._v("Deploy mô hình lên cloud để sử dụng.")]),n._v(" "),t("h3",{attrs:{id:"giam-sat"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#giam-sat"}},[n._v("#")]),n._v(" Giám sát")]),n._v(" "),t("p",[n._v("Cuối cùng là giám sát xem mô hình chạy có ổn hay không? Chỗ nào cần sửa chữa bổ sung, hoặc mô hình dự đoán bị yếu phần nào,\nmạnh phần nào? Bằng việc phân tích này, ta sẽ bổ sung dữ liệu phù hợp để học nhằm cải thiện độ chính xác.")])])}),[],!1,null,null,null);h.default=a.exports}}]);